mapper.select:
SELECT t.material FROM t_sms_purchase_order i INNER JOIN t_sms_purchase_order_entry t ON t.parent = i.id WHERE 1=1
and i.supplier = #{supplier}
and i.tickDate >= #{orderStartDate}
and i.tickDate <= #{orderEndDate}
and t.material <= #{material}
group by t.material

SELECT t.id, t.qty, t.parent, t.material, t.invoiceQty, w.actualQty, r.returnQty, t.unit, i.tickDate FROM t_sms_purchase_order i INNER JOIN t_sms_purchase_order_entry t ON t.parent = i.id LEFT JOIN t_sms_purinwarehs_entry w on t.parent = w.orderId and t.material = w.material LEFT JOIN t_sms_purreturns_entry r on t.parent = r.orderId and t.material = r.material WHERE 1=1
and i.supplier = #{supplier}
and i.tickDate >= #{orderStartDate}
and i.tickDate <= #{orderEndDate}
and t.material <= #{material}
